INSERT INTO `list` VALUES (39, '修罗战神：龙婿（又名：逍遥战神在都市）', '三年蛰伏，真龙抬头！所有人都认为我是个废物，但只要你点头，万里山河做嫁衣，我许你一世荣光！', '顾白本尊', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1188808246654.jpg');
INSERT INTO `list` VALUES (40, '最强赘婿归来', '三年前，他入赘徐家，受尽欺辱、深渊度日...她说你窝囊一世，路边野花....三年后，他王者归来，冷笑九月初八来秋日，百花杀尽独我花。', '灵枭', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1100517984704.jpg');
INSERT INTO `list` VALUES (41, '械瞳', '有人说，他是一个天才，有人说他是一个靠战争发财的军火贩子，可更多的人，称他为联邦英雄。一个原本机械工程系的天才，一场被人无耻抢夺研究成果的杀局，一只机械瞳所引发的阴谋，看我们垃圾堆里的荀小哥如何逆袭重新夺回属于他的荣耀，以及成为地球联邦最大的军火商。', '荀小哥', '商场', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1122507995128.jpg');
INSERT INTO `list` VALUES (42, '龙婿为王', '两年前，他掌兵百万，一声号令西疆！但天妒奇才，遭奸邪陷害，沦为过街老鼠的禽兽女婿。当复仇归来的一刻，岳母当场跪地大哭：“龙婿大人，我错了，求求你别抛弃我女儿！”', '吹牛笔', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1157968246240.jpg');
INSERT INTO `list` VALUES (43, '战神帝婿', '十三年前，家破人亡，十三年后战神回归！傲骨嶙峋！铁杆长枪！为国，我能奋勇杀敌为家，我能手刃仇人！铮铮铁骨！宁折不屈！', '苹果味儿果汁', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1147327993128.jpg');
INSERT INTO `list` VALUES (44, '都市之长生至尊', '灵气复苏，异能觉醒，世界大战，地球被炸回了石器时代。经过一千年的休养生息，人类终于取回了文明，但却是虚假的文明。而此时，千年前消失的无敌战神终于归来。他看着后人为自己修建的雕像，深思了一会儿，然后感叹道：“我原来这么帅？”', '夏虫不冰', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1106658251726.jpg');
INSERT INTO `list` VALUES (45, '超凡圣医在都市', '大学生陆舟被前女友陷害，坐了四年牢。四年后，陆行舟从监狱中走出，获得惊天医术和无敌武学，誓要复仇雪恨，吐尽心中不平之气！', '无量', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1189867783244.jpg');
INSERT INTO `list` VALUES (46, '乡村小仙农', '小农民张小龙意外得到一个戒指空间，从此开启灿烂前程。这个戒指空间内，有着一片无边无际的大海，大海中间有一块小土地，而且拥有一个神仙的喷泉，喷泉里的水，有无穷的妙用，什么强身健体，美白肌肤，治病救人……', '姜敏', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/201705221457179539.jpg');
INSERT INTO `list` VALUES (47, '超凡圣医在都市', '大学生陆舟被前女友陷害，坐了四年牢。四年后，陆行舟从监狱中走出，获得惊天医术和无敌武学，誓要复仇雪恨，吐尽心中不平之气！', '无量', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1118517918210.jpg');
INSERT INTO `list` VALUES (48, '最强女婿', '入赘三年，受尽无尽嘲讽。原本林琦认为这一天依旧会被重置，继续进行着重复。但无形中打破时间枷锁，从此开始一飞冲天！', '三十先生', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1197338227220.jpg');
INSERT INTO `list` VALUES (49, '无双姑爷', '入赘三年，受尽无尽嘲讽。原本林琦认为这一天依旧会被重置，继续进行着重复。但无形中打破时间枷锁，从此开始一飞冲天！', '三十先生', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1167538238201.jpg');
INSERT INTO `list` VALUES (50, '赘婿当道-龙王殿（又名豪门战神逍遥战神龙门兵少）', '西部狼烟，烽火连天。楚天寒，当世战神，率苍龙铁军，为护国运昌盛，怒发冲冠，一去不回。三年之后，他是上门女婿，坐拥江州第一美女为妻。余生数载，只想平凡而过，功名皆云烟。然而，却受万众嗤笑，人人踩之。奸臣当道，大功独揽，世家争锋，只手遮天。红尘滚滚，几多浮沉，我怜君中宵舞，王者归来，山河为画，谱写史诗传奇。', '一壶酒', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1179948259220.jpg');
INSERT INTO `list` VALUES (51, '神婿无双', '废物赘婿，饱受羞辱。    萧寒沉默了三年，剑锋也藏了三年。    三年后，时机到来！    藏剑出鞘，锋芒注定要闪耀九州！', '风起陆离', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1161898255627.jpg');
INSERT INTO `list` VALUES (52, '冰山女总裁的贴身战神', '七年的杳无音信，换来如今的强势回归。曾经失去的，他要亲手拿回，曾经遭受的，要百倍奉还！敢动我的女人？做好当太监的准备吗？还想打我女儿主意？是否做好面对地狱的准备？什么，你喜欢这个冷艳高贵的女总裁？对不起，他是我的。你还喜欢这位英姿飒爽的军花？不好意思，我的人。', '消亡丶', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1173867089483.jpg');
INSERT INTO `list` VALUES (53, '至尊战神女婿', '他在北疆立下不世功业，被封武威战神，如今为妻归来，甘当上门女婿，只为她撑起一片天！', '心安之处', '轻松', '1', '1', 'http://img-tailor.11222.cn/bcv/middle/201603311324445820.jpg');
INSERT INTO `list` VALUES (54, '超级丹帝重生都市', '呦，重生了，没事儿，我有修真仙法。　　呦，富二代想抢夺我家的地产，没事儿，我有仙法。　　呦，有术法高人想夺走天材地宝，没事儿，我有仙法。　　你说你权势滔天，没事儿，我一针要你跪下。　　你说你家财万贯，没事儿，我一粒丹药要你一亿。　　你说你有武道宗师，没事儿，我一掌把你拍进地底。　　你说你有神境至尊，没事儿，天地玄法皆听我令！　　你说你有天材地宝从不给人，没事儿，杀了你们，就是我的了！　　渡天劫重生之后的韩振，将以无敌的姿态，碾压众生。　　嗯嗯嗯，你说的很有道理！　　可是，我为什么要和你讲道理？', '凤千城', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1127388254026.jpg');
INSERT INTO `list` VALUES (55, '长生赘婿', '上门女婿，被女方家人欺压，为了公司，家族人联合起来排挤女主，想要把她从公司踢出去，随后姬无名开始了护妻之路。', '铁甲大宝', '轻松', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1142727969805.jpg');
INSERT INTO `list` VALUES (56, '保镖凶猛', '脸皮一厚，征服宇宙。藏不露，一拳一个小朋友。浪荡江湖，必须心狠手毒。苏漾同学的原则是：说最稳的话，干最狠的架。失忆高手重回巅峰，就从做美女最凶悍的保镖开始吧……', '断莫离', '轻松', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1160208264661.jpg');
INSERT INTO `list` VALUES (57, '天下第一', '老婆看不起我，当她的美艳闺蜜勾引我时，她开始慌了……', '唯我独尊', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1174747899727.jpg');
INSERT INTO `list` VALUES (58, '乡村小仙农', '小农民张小龙意外得到一个戒指空间，从此开启灿烂前程。这个戒指空间内，有着一片无边无际的大海，大海中间有一块小土地，而且拥有一个神仙的喷泉，喷泉里的水，有无穷的妙用，什么强身健体，美白肌肤，治病救人……', '姜敏', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1121657980764.jpg');
INSERT INTO `list` VALUES (59, '天下第一', '老婆看不起我，当她的美艳闺蜜勾引我时，她开始慌了……', '唯我独尊', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1139777989107.jpg');
INSERT INTO `list` VALUES (60, '重生之特战兵王', '狗行千里吃屎，狼行千里吃肉。重生前，左启是全华夏最顶尖的特战兵王；重生后，再看他如何纵横都市，恣意人生。', '冷箫', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1156538021719.jpg');
INSERT INTO `list` VALUES (61, '极品兵婿', '他，绰号“绝命修罗”，仅凭他的名字就足以让敌人闻风丧胆！他，拥有惊天医术，一手《开天针法》出神入化，无论什么疑难杂症都能手到擒来！他，王者归来，却阴差阳错成了三流豪门的上门女婿。谁说上门女婿一定是地位低下，受尽万般白眼和屈辱？林南……活生生将上门女婿当成了大爷！', '真剑', '轻松', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1199838002969.jpg');
INSERT INTO `list` VALUES (62, '文娱：请别叫我大佬', '当张远用‘这是一个大明星’当做自己的昵称时，      娱乐圈所有人都觉得张远在自我调侃。      不屑和鄙视纷涌而至。      可当他真的成为了一个大明星时，人们却称呼他为毒舌大佬！', '我是一名大作家', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1109527981317.jpg');
INSERT INTO `list` VALUES (63, '第一神婿', '长生十万年，一朝被害重生成赘婿。父母被害，老婆被虐；兄弟背叛，家人羞辱。以前每个人都看不起我，以为我是吃软饭的窝囊废。现在我浴火重生，我要向世人宣布，这天是我的，这地也是我的。兄弟我可以让你飞黄腾达，敌人我可以让你身败名裂。', '文九', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1183497987333.jpg');
INSERT INTO `list` VALUES (64, '开挂临时工', '孤儿男神临时工，一路开挂赢人生！古怪女神脾气怪，降服男神护夫狂！', '魔珏', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1192877993160.jpg');
INSERT INTO `list` VALUES (65, '无敌：从女装大佬开始', '得到个神级变身系统，每天早上起来都要变成不同的身份。猫女郎，赛亚人，绿巨人，奥特曼……很累诶，还能不能让我好好把妹了？', '水门绅士', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1142818265975.jpg');
INSERT INTO `list` VALUES (66, '我既王', '村子被平，母亲上吊而死。昔日窝囊女婿，如今王者归来，牧云发誓，定要屠尽仇敌！', '牧云', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1110847957210.jpg');
INSERT INTO `list` VALUES (67, '重生1994：大国崛起', '回到1994，除了挽救即将破碎的家，上天还交给罗立一个重要任务......这是最好的时代。一切即将改变......', '云少番茄', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1114637959346.jpg');
INSERT INTO `list` VALUES (68, '当医生开了外挂', '刘浩，国内医科学院的应届毕业生，他对自己的人生规划就是安安稳稳的在一家不错的医院，寻一份称心的医生工作，然后拿着稳定的工资收入，娶妻生子，安慰且平凡的度过一生。可没想到，在一次偶然的时间，随着一个“叮咚”声音的响起，他的人生轨迹开始有了转变。', '浅笙一梦', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1124208164676.jpg');
INSERT INTO `list` VALUES (69, '上门龙婿', '普通的乡下之人叶峰为母治病，做了三年的上门女婿，受尽白眼和侮辱依旧没能救回母亲。谁知母亲走后，却留下了一封信，从此改变了他的人生，开启了逆天之路，走上人生巅峰。', '洛少', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1162177947998.jpg');
INSERT INTO `list` VALUES (70, '乡村小仙农', '小农民张小龙意外得到一个戒指空间，从此开启灿烂前程。这个戒指空间内，有着一片无边无际的大海，大海中间有一块小土地，而且拥有一个神仙的喷泉，喷泉里的水，有无穷的妙用，什么强身健体，美白肌肤，治病救人……', '姜敏', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1123607414082.jpg');
INSERT INTO `list` VALUES (71, '当医生开了外挂', '刘浩，国内医科学院的应届毕业生，他对自己的人生规划就是安安稳稳的在一家不错的医院，寻一份称心的医生工作，然后拿着稳定的工资收入，娶妻生子，安慰且平凡的度过一生。可没想到，在一次偶然的时间，随着一个“叮咚”声音的响起，他的人生轨迹开始有了转变。', '浅笙一梦', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1141447988195.jpg');
INSERT INTO `list` VALUES (72, '上门龙婿', '普通的乡下之人叶峰为母治病，做了三年的上门女婿，受尽白眼和侮辱依旧没能救回母亲。谁知母亲走后，却留下了一封信，从此改变了他的人生，开启了逆天之路，走上人生巅峰。', '洛少', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1116197983891.jpg');
INSERT INTO `list` VALUES (73, '女总裁的超级保镖', '一代地下王者回归都市，竟被父亲以死逼婚。虽然媳妇是个万人迷的女总裁，但因为性子高冷，他决定拒婚。', '夜听风', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1179108160841.jpg');
INSERT INTO `list` VALUES (74, '为民不悔', '商场惨败的夏云杰，弥留之际竟然重生回到了十六年前。这一次，他决定放弃从商，重新走回仕途。他要通过自己的努力，带着贫困的家乡百姓，在改革开放的大好时代迎头赶上……', '何千叶', '商场', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1191337971947.jpg');
INSERT INTO `list` VALUES (75, '重生之修仙赘婿', '从修真界归来，赵凡已然是牛逼轰轰的角色。只不过他很低调，只想把全世界最好的一切都给自己漂亮的老婆。赵凡表示：“咳咳，不好意思，我已经名草有主了!”', '三猪', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1142117974524.jpg');
INSERT INTO `list` VALUES (76, '最强赘婿：神级选择', '某天，一个美女总裁找上季枫。“我怀孕了，孩子是你的，签下这份合同，我俩去民政局领证正式成为合法夫妻。“看着这份上门女婿的合同，季枫无奈，就那一次，怎么就那么准。好在，神级选择系统启动成功。“选择一，签下这份合同，您将获得新手大礼包一份。”“选择二，拒绝这份合同，系统解绑您将获得五百万解绑补偿。”“我选择一。”季枫惊喜的发现，因为这份合同，自己老爸老妈终于向他摊牌多年来的隐藏身份了......', '在下二十三', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1133877969759.jpg');
INSERT INTO `list` VALUES (77, '万人之上', '战神回归，发现女儿被抽耳光，妻子被逼下跪，一声令下，屠敌满门！！', '多喝白开水', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1124977027359.jpg');
INSERT INTO `list` VALUES (78, '最强狂婿', '他是世家的少爷，却入赘给人当女婿。他顶着窝囊废，受尽各种凌辱，却隐忍不发。只为！默默守护自己的妻子！', '叶按', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1161626967760.jpg');
INSERT INTO `list` VALUES (79, '桃源首富', '连天大旱要人命，二十出头没出息，一朝寻得农政书，万般术法惊人心，种田、开山、办鱼塘……一脚踹开恶人……，治田治人治天下，世间权贵皆傍我。醒掌权，醉卧膝，美人怜，天下谁人不识君?“张见！”“喊我全名你也配?”', '一指墨', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1162427983918.jpg');
INSERT INTO `list` VALUES (80, '都市无敌战神', '少年百战封神，成为百将之首！而今得知兄弟被害，以雷霆狂扫之势归来宁川，谁人可挡！', '浮华余生', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1155738254459.jpg');
INSERT INTO `list` VALUES (81, '开局一套海景别墅', '一通自动接听的电话，带着萝莉音的系统，小中介王黎被宣布中奖一套豪华别墅！摇身一变成为了包租公的王黎接下来干什么？自然是教训欺压自己的垃圾经理，寻找合适的美女租客，顺便豪气打脸大学同学……要问王黎目前的小目标是什么，先把自己的别墅盖成摩天大楼吧，有钱人的生活真是“枯燥”啊。', '会圆果汁', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1109938252526.jpg');
INSERT INTO `list` VALUES (82, '超级奶爸至尊', '一代仙尊归来，不但成了赘婿，还有了一个宝贝女儿。女儿：我爸一生行事，何须向尔等蝼蚁解释！女儿：爸爸，我被人欺负了，你快去将他们的狗头打烂！　　女儿：爸爸，我要看烟花！星星爆炸的那种烟花！叶尘无奈道：“念念，爸爸是仙尊，没事就打暴星星给你看这种事情，有失身份，而且太阳系就剩下六个星星了，我不能再灭了……好好好，最后一次看烟花，好了，就剩下五个星星了。”', '不酸的柠檬', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1169767988379.jpg');
INSERT INTO `list` VALUES (83, '超级奶爸至尊', '一代仙尊归来，不但成了赘婿，还有了一个宝贝女儿。女儿：我爸一生行事，何须向尔等蝼蚁解释！女儿：爸爸，我被人欺负了，你快去将他们的狗头打烂！　　女儿：爸爸，我要看烟花！星星爆炸的那种烟花！叶尘无奈道：“念念，爸爸是仙尊，没事就打暴星星给你看这种事情，有失身份，而且太阳系就剩下六个星星了，我不能再灭了……好好好，最后一次看烟花，好了，就剩下五个星星了。”', '不酸的柠檬', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1184538250542.jpg');
INSERT INTO `list` VALUES (84, '豪门战神：我既王', '一别多年。再次归来，昔日少年已是镇守边疆的王！为报兄弟之仇，回归都市。当年之仇敌，定要一一清算！', '双生子', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1183067956785.jpg');
INSERT INTO `list` VALUES (85, '都市至尊豪婿', '江东豪门王家独女，发下宁愿嫁给乞丐不愿意让父母安排婚事的誓言，负气嫁给路边年轻乞丐，没有想到，捡来的女婿竟然是亿万富豪的唯一继承人！为了守护自己的妻子，江圣凌终于和爷爷冰释前嫌，成为商业帝国的继承者，挺身而出，斗败江东四大家族的阴谋。而这时，更大的危险逐渐逼近……', '亚光', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1129377806540.jpg');
INSERT INTO `list` VALUES (86, '都市巅峰高手', '一个女人莫名其妙要我负责，不答应都不行。', '明妖', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1157797847453.jpg');
INSERT INTO `list` VALUES (87, '都市最强女婿', '江尘穿越了，穿越到了平行世界。同时，还拥有了时间管理系统。“既然我能掌控时间，从今天开始，我就是时空之神！”', '稻花儿香', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1161407865713.jpg');
INSERT INTO `list` VALUES (88, '和女神荒岛求生的日子', '一场空难，和陈锋一同活下来的竟然是女神总裁！凭借熟练的求生技能，让原来刻薄的美女上司对他五体投地！别人的荒岛艰难逃生，陈锋却是开启了幸福生活！', '七罪', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1100248227619.jpg');
INSERT INTO `list` VALUES (89, '史上最强龙婿', '上门女婿李春龙，意外觉醒上古记忆，并收到神豪老爷子送的黑金卡，从此医武双绝，财力惊人，不仅赢得娇妻芳心，更是成了强势崛起的一代传奇……', '提拉迷苏', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1116868263603.jpg');
INSERT INTO `list` VALUES (90, '都市至尊狂仙', '对于天才而言，修行往往是吃饭喝水那么简单，作为一代仙王转生，林肃原本也是这么以为的。直到他拿到了那本《狂人战天法》，得知修行要靠不断装X打脸来积攒所谓的狂气时，强者重修的异样之路便就此开始！', '月下蓑衣客', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/middle/201603311324445820.jpg');
INSERT INTO `list` VALUES (91, '超品兵王在都市', '超级兵王回归都市，本想单纯的保护女老板，却意外陷入了血雨腥风。面对各类阴谋阳谋，且看他如何斩破荆棘，踏上巅峰。', '月不醉', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1122247944296.jpg');
INSERT INTO `list` VALUES (92, '最强赘婿', '被逼入赘，本以为捡个大便宜，白得一美女老婆。 可入赘后才知，豪门日子难混，什么时候是个头？ 一朝崛起，山鸡变凤凰，九龙冲云宵。 管它豪门深几许，双手一握，皆擒来。', '彼岸无悔', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1193997916787.jpg');
INSERT INTO `list` VALUES (93, '做首富从每日秒杀开始', '“兰博基尼、毒药只要一块钱！”“太极拳，八极拳这些只要十块?”“卧槽，张角传承?钟馗传承?”这手机到底还能给我什么惊喜！', '揽二乔于东南兮', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1109737791653.jpg');
INSERT INTO `list` VALUES (94, '乡村小仙农', '小农民张小龙意外得到一个戒指空间，从此开启灿烂前程。这个戒指空间内，有着一片无边无际的大海，大海中间有一块小土地，而且拥有一个神仙的喷泉，喷泉里的水，有无穷的妙用，什么强身健体，美白肌肤，治病救人……', '姜敏', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1163108246483.jpg');
INSERT INTO `list` VALUES (95, '做首富从每日秒杀开始', '“兰博基尼、毒药只要一块钱！”“太极拳，八极拳这些只要十块?”“卧槽，张角传承?钟馗传承?”这手机到底还能给我什么惊喜！', '揽二乔于东南兮', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1160738054673.jpg');
INSERT INTO `list` VALUES (96, '末世之开局就无敌', '一次意外收到礼物，里面装是已经停运并被军方封锁数年的“末日战场”游戏登录装备，多年前游戏内测中唯一存活下来的云飞，无意的使用这套设备重新开启了游戏。生存与利益，人类的欲望，本性全部暴露在这场游戏中，云飞立誓要在这场游戏中生存下去~', '星空夏目', '网游', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1100168237037.jpg');
INSERT INTO `list` VALUES (97, '我的姐姐是战神', '本来只想安安静静的读个大学，却被逼着签各种红头文件……唐河：我能怎么办，我也很无奈啊，这种躺赢的人生，我也不想要……谁让我的姐姐是巾帼英雄，无敌战神，疯狂扶弟魔……', '林海小老虎', '爽文', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1149088244990.jpg');
INSERT INTO `list` VALUES (98, '我的美女总裁未婚妻', '一代佣兵之王林轩回归都市，在遭遇女友的背叛之后，却意外和冷艳总裁签下一纸合约，成为总裁的男友。 从此之后，各种事件络绎不绝的闯进林轩生活之中，让想过平静生活的他怎么也平静不下来。', '夜夜玩翻滚', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1133838019383.jpg');
INSERT INTO `list` VALUES (99, '大医直播间', '六尺手术台，三寸柳叶刀！赢，病人活！输，病人死！实习小医生偶获手术直播系统，从此横扫医学界，妙手回春，活人无数！', '三寸柳叶刀', '轻松', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1130288265936.jpg');
INSERT INTO `list` VALUES (100, '天降狂婿', '\"三少爷，叶家的复兴就指望你了。”“什么，你要入赘唐家？当人家的上门女婿？”“”你丫烦不烦，管的着吗，小爷为了俺家老婆，可以放弃整个天下！”是金子总会发光的，叶轩要向那些世俗者证明，上门女婿也可以当出新高度，赘婿也要做那个独一独二的一品豪婿。', '薄情龙少', '热血', '1', '1', 'http://img-tailor.11222.cn/bcv/big/1106688252228.jpg');
INSERT INTO `list` VALUES (101, '最强狂兵', '一代兵王含恨离开部队，销声匿迹几年后，逆天强者强势回归都市，再度掀起血雨腥风！简单粗暴是我的行事艺术，不服就干是我的生活态度！看顶级狂少如何纵横都市，书写属于他的天王传奇！依旧极爽极热血！', '烈焰滔滔', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1173428007543.jpg');
INSERT INTO `list` VALUES (102, '超级女婿（又名：豪婿、贵婿临门、华丽逆袭）', '主角：韩三千 苏迎夏入赘三年，所有人都以为可以骑在我头上。而我，只等她牵起我的手，便可以给她整个世界。本书又名《豪婿》', '绝人', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1162198030608.jpg');
INSERT INTO `list` VALUES (103, '我的绝色总裁未婚妻', '主角：沈浪 苏若雪【最火爆畅销书】本书又名《神级龙卫》神秘高手龙潜花都，与冰山美女总裁签订婚约，但无奈被嫌弃。可怜的沈浪，只得外出觅食。不料一个个美女接踵而至，沈浪陷入各种桃运漩涡。当然，最主要的还是征服冰山女总裁。不乖？调教一下就好。', '花幽山月', '异能', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1131877993862.jpg');
INSERT INTO `list` VALUES (104, '都市之最强狂兵', '国之重器，猛虎出笼！亦正亦邪的他注定有着无法平淡的命运！身负枷锁执掌生杀命轮！他身立潮头一生高唱大风！只装最牛的逼，只踩最狠的人！他一生辉煌，彪悍铸就，舞练长空！！！', '大红大紫', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1191228181170.jpg');
INSERT INTO `list` VALUES (105, '赘婿当道（又名：神都猛虎、神级狂婿、旷世神婿）', '主角：岳风 柳萱岳母：你赶紧离开我女儿，你个废物东西，配不上她。三日后。岳母：我求求你别离开我们家，行吗？', '吻天的狼', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1156758259286.jpg');
INSERT INTO `list` VALUES (106, '龙王殿（又名：上门狂婿）', '主角名：张玄、林清菡地下世界王者Sata，做了总裁家的女婿。本书又名《龙王殿》、《生而为王》、《无敌神婿》、《顶级强者》', '一杯八宝茶', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1130507986755.jpg');
INSERT INTO `list` VALUES (107, '都市之逆天修仙', '渡劫期超级强者楚默重生少年。地下世界暗潮涌动，古武传人，巫蛊传人，邪物，险地，女巫，炼金术师……等等上一世所不知道的超级力量渐渐浮出水面，东西方诸神降临撼动世界？不好意思，一拳打爆！娇贵千金苦苦暗恋？冰山总裁倾家荡产倒贴？时尚女王主动抛出橄榄枝？全球影后当众表白？不好意思，你们还不配！', '睡神', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1187517996960.jpg');
INSERT INTO `list` VALUES (108, '回到地球当神棍', '他，曾经失踪八年。可没有人想到，他是穿越到了异界，并且修炼了八千年！他，拐跑了豪门千金。可没有人想到，他在拐跑千金的同时，顺便灭了人家的无良未婚夫。他，在寻找妹妹。可没有人想到，他在找妹妹的过程中，同时干翻了地球上的所有大势力。他能炼丹，会打架，若是有人惹了他……', '勿小悟', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1145958246229.jpg');
INSERT INTO `list` VALUES (109, '我的白富美老婆', '再一次侮蔑之后，秦城总算觉醒了。曾经不曾得到的，这次要一一握在手里。', '城南一梦', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1188198191798.jpg');
INSERT INTO `list` VALUES (110, '女神的超级赘婿（又名：我给女神当赘婿、超级赘婿）', '（主角名：林阳、苏颜）（又名：圣手神医、王婿、医道狂尊、女神的上门贵婿）我遵循母亲的遗言，去给别人做上门女婿，为期三年。现在，三年时间结束了...', '黑夜的瞳', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/201805081502392307.jpg');
INSERT INTO `list` VALUES (111, '最佳女婿(最佳赘婿)', '（主角名：林羽、江颜）人死的时候会有意识吗？会，因为我经历过。这个世界上真的有灵吗？有，因为，我就是。重活一次后，发现他有一个美到窒息的老婆，这纠结了……', '陪你倒数', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1152688031337.jpg');
INSERT INTO `list` VALUES (112, '我的极品小姨', '李南方躺在酒店的浴缸里，正在舒服的泡澡，忽然一个美女闯进来，拿枪指着他的头逼他……后来他才知道，美女居然是他小姨。', '风中的阳光', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1198977990156.jpg');
INSERT INTO `list` VALUES (113, '重生之都市狂仙', '剑斩风雷遮日月，掌握乾坤捣幽冥。世间人有六十亿，唯我一人称骄狂。 仙界青帝重生于灯红酒绿，纸醉金迷的繁华都市。从此一个前世的都市失败者，彻底翻身，走上了一条让惊得天下纨绔皆惶恐，惹得世间美人尽倾心的绝世之路。世人笑他猖，世人笑他狂，万古之后的天地，世人几经轮回，而他依然在那，高高在上，俯视众生。', '梦中笔丶', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1188247502896.jpg');
INSERT INTO `list` VALUES (114, '极品透视', '醒掌天下权，醉卧美人膝，乃是每一个男人的终极目标。一个普通青年，从一个老骗子的手中得一奇异石头，令人惊奇的是，借助石头，他竟然拥有了透视的能力，且看他如何玩转都市，笑傲人生。', '赤焰圣歌', '奇遇', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1195946944649.jpg');
INSERT INTO `list` VALUES (115, '乡村最强小神农', '昔日高考状元苏扬因为女朋友打架被学校开除，随后得了怪吃病不得不回到农村老家，两年后居然生出一条龙来，命运随之得到改变！种灵田，开饭店，养神宠，植灵药，医百病……看贫困小子一步步走向强大，将阻挡他的敌人一个个踩在脚下，缔造强大的商业帝国，将各路美女揽在怀中，打造乡村最强小神农传奇！', '龙腾青山', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1113398241916.jpg');
INSERT INTO `list` VALUES (116, '顶级神豪', '（又名：傲视无双、一世巅峰）（主角名：林云、黄梦怡、林炎）女朋友嫌林云穷，跟着富二代跑了，结果突然冒出个首富外公来跟林云相认。“你为什么现在才来跟我相认，我就是饿死，死外边，也绝对不会跟你相认的！”“叮，银行卡到账一亿！”“嗯，真香……”成为富三代后，林云渐渐明白一个真理，有钱真的可以为所欲为！', '北辰本尊', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/201708211745464850.jpg');
INSERT INTO `list` VALUES (117, '胜者为王', '辱我者，欺我者，害我者，十倍奉还！我不懂年少轻狂，只知道胜者为王！', '唐九', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1110708240314.jpg');
INSERT INTO `list` VALUES (118, '极品风流医仙', '陈风和女神一夜邂逅，觉醒前世记忆，从此一手惊天医术，一手绝世神通，豪门世家任我踩，校花女神入我怀！', '风化羽', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1197438255286.jpg');
INSERT INTO `list` VALUES (119, '超级兵王', '他是雇佣兵世界的王者，他是令各国元首头疼的兵王！为朋友，他甘愿两肋插刀；为亲人，不惜血溅五步！是龙，终要翱翔于九天之上，携风云之势，一路高歌猛进，混的风生水起。', '步千帆', '扮猪吃虎', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1184848263886.jpg');
INSERT INTO `list` VALUES (120, '绝品透视', '他是威震国际的武学宗师，武道神化，十步杀一人；他是温文尔雅的古玩大家，赌石赏画，收藏富可敌国；他是鼎鼎大名的玄门高手，能断风水，善察吉凶，让佛道两教共尊。他更是医道圣手，侦察之王，投资奇才!他便是张均，因机缘巧合之下，得到佛陀眼珠舍利，从此遍阅世间美人，傲视天下！', '小妖', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1104348236784.jpg');
