INSERT INTO `list` VALUES (137, '捡漏', '一眼百年，重生都市。盛世华章，古董收藏。秦碑周彝，金石字画。青铜青花，翡翠美玉。天下奇珍，尽在我手。重生归来的金锋在现代都市，凭借神乎其技的鉴宝本领，一步步走向巅峰。发扬民族最传统的文化，传承千年最完整的文明。', '金元宝本尊', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1150708002634.jpg');
INSERT INTO `list` VALUES (138, '不败神婿：盖世战神（不败战神）', '上门女婿被迫从军，八年浴血，百战封神。一朝回归，都市再起风云。萧天忌：从现在起，这个家我说了算。你们的生死，由我掌控。', '腹肌', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1173758234878.jpg');
INSERT INTO `list` VALUES (139, '极品花都医仙', '（又名：都市至尊仙医、神秘少年闯都市、神针侠医）（主角名：陈飞宇、苏映雪）一纸婚约，陈飞宇入世寻找未婚妻，从此奇遇不断。他左手金针度世，右手长剑破敌，赚钱救人两不误。惹我者打！爱我者疼！众多极品女神纷至沓来，芳心暗许。师傅，我要上山，这都市生活让我老腰扛不住啊……', '执笔问长生', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1116046931348.jpg');
INSERT INTO `list` VALUES (140, '随身带着个世界', '双失青年陈武无意中得到了一个空间世界，空间内植物生长速度翻倍，能让动物增加灵性，无聊时卖卖菜，养养鱼，种种人参，遛遛狗，钞票在手，人间都市，任我逍遥。', '疯狂土豆', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1182248246494.jpg');
INSERT INTO `list` VALUES (141, '透视乡村小神医', '村少年意外获得超级系统，一个只想种种田，泡个村花当老婆的小村医，从此踏上了传奇之路。他拥有无数个身份，神医，神农，超级科学家。无数的商人开始巴结他，无数的美女开始追求他。美女总裁，美女杀手，美女警察，美女校花，各式各样的美女前赴后继的冲向他的村子······', '鲤鱼飞起来', '异术超能', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1134628252515.jpg');
INSERT INTO `list` VALUES (142, '今夜花开', '一个长期被女人玩弄的傻子因为一次意外突然变聪明，却选择继续装傻，这就好玩了……别人笑我太疯癫，我笑他人看不穿！', '月满小楼', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1171167011299.jpg');
INSERT INTO `list` VALUES (143, '都市无敌战神', '（又名：北境战神、猛龙战枭、超级战神在都市、我生为王）（主角名：林北、林天策）五年前，被陷害入狱！五年后，他荣耀归来，天下权势，尽握手中！我所失去的，终会千百倍的拿回来！', '我不是z', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1192427989125.jpg');
INSERT INTO `list` VALUES (144, '大国轻工', '一位来自21世纪的女鞋设计师杨立民穿越到1979年，成为了一名光荣的鞋厂技术员。凭借着高超的技术和先进的管理理念，杨立民带领着勤劳的工友们一起挥洒着汗水和青春，打造出一个享誉世界的轻工业帝国。衣车转动、烤箱轰鸣……书写着一个时代无悔的工业之魂！', '蜀越', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1165108249516.jpg');
INSERT INTO `list` VALUES (145, '终极教官', '铁拳所向，试问谁可争锋？义胆柔情，各色美人争宠！他，就是终极教官，当世大魔王！为美人无限张狂；为兄弟两肋插刀；为亲人誓死守护！尸山血海中杀伐，累累白骨中踏步，蓦然回首，已傲立巅峰，成就传奇霸业！', '梁七少', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1199128016707.jpg');
INSERT INTO `list` VALUES (146, '鬼医神农', '【百万读者推荐！阿里精品好书！】医术能救人，也能杀人。李林获得神秘空间，医术能力提高，开始游走乡村城市中间，看各种美女，杀各种坏人，救该救之人。', '三尺神剑', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1120428014069.jpg');
INSERT INTO `list` VALUES (147, '大国轻工', '一位来自21世纪的女鞋设计师杨立民穿越到1979年，成为了一名光荣的鞋厂技术员。凭借着高超的技术和先进的管理理念，杨立民带领着勤劳的工友们一起挥洒着汗水和青春，打造出一个享誉世界的轻工业帝国。衣车转动、烤箱轰鸣……书写着一个时代无悔的工业之魂！', '蜀越', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1183458241412.jpg');
INSERT INTO `list` VALUES (148, '终极教官', '铁拳所向，试问谁可争锋？义胆柔情，各色美人争宠！他，就是终极教官，当世大魔王！为美人无限张狂；为兄弟两肋插刀；为亲人誓死守护！尸山血海中杀伐，累累白骨中踏步，蓦然回首，已傲立巅峰，成就传奇霸业！', '梁七少', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1142227796890.jpg');
INSERT INTO `list` VALUES (149, '鬼医神农', '【百万读者推荐！阿里精品好书！】医术能救人，也能杀人。李林获得神秘空间，医术能力提高，开始游走乡村城市中间，看各种美女，杀各种坏人，救该救之人。', '三尺神剑', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1116258022542.jpg');
INSERT INTO `list` VALUES (150, '第一赘婿', '（又名：医途崛起）（主角名：秦立、 楚清音）父母失踪，为得到能力找到他们，我迫不得已要当十年哑巴。却在这档口被点名做了上门女婿，虽然老婆绝美倾城，但从未给过我一天好脸色，说我是个窝囊废！而今天，十年限制结束！我将把一切，重新翻盘……', '山村小伙夫', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1187248186920.jpg');
INSERT INTO `list` VALUES (151, '都市弃少兵王', '“他们都叫我天才杀手。”萧毅把军刺放入右腿的刀鞘里，弹了弹烟灰，一脸轻松地说：“我还有一个称呼，叫做美女收割机。”说完，他换了个坐姿对着摄像机。“当然，如果发生过什么不好的行为，比如干掉很多天才或者和各类型美女做点什么。真的，请相信我，每一次都不是我主动的。”萧毅说话的时候，真诚的让人不得不信。台下的观众却是一片哄笑，纷纷露出了鄙夷的神色。知性美女记者也笑场了，“很期待呢。”', '流水今日', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1190088250449.jpg');
INSERT INTO `list` VALUES (152, '龙王殿', '生而为王，年少父母被杀，他消失数年，成为世界地下世界的超级王者回归都市，这次回来，看他如何改变天下大势，成为王者至尊。', '东风不破', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1175798265849.jpg');
INSERT INTO `list` VALUES (153, '最强保镖', '史上最强保镖，最热血的保镖，最冷酷的保镖。然而，他却是所有保镖的敌人。', '海洋', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1162418241219.jpg');
INSERT INTO `list` VALUES (154, '被开除后我养了一条龙', '“土包子，滚回农村种田，我动动手指都能玩死你！”“这么大的人靠父母养活，还把家里吃穷了，是我都没脸活下去！”女友被欺负，高考状元上门出头却被学校开除。怪病缠身，不得不回到农村老家。直到有天神龙傍身，各界大佬纷纷捧着钞票上门……', '龙腾青山', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1138897974430.jpg');
INSERT INTO `list` VALUES (155, '最强兵王归来', '东方有雄狮崛起，一怒震九州！一代兵王回归都市，生于燕京纨少之身！一纸婚约，一段绝世之恋……战神之路，从此开始！', '青锋石上', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1192848239472.jpg');
INSERT INTO `list` VALUES (156, '君临都市', '（又名：八荒战神）（主角名：陈八荒、方静）未婚妻一家人太过分，结婚当天坐地起价多要三十万彩礼，我义无反顾转身娶了冷艳的伴娘，谁知道婚后惊喜连连……', '大火', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1125917450468.jpg');
INSERT INTO `list` VALUES (157, '都市潜龙', '（又名：美女的超强近卫、美女总裁爱上小保安、战龙神卫）（主角名：赵东、苏菲）赵东是名夜班保安，有一天送宿醉晚归的女业主回家，结果被对方强推……', '西装暴徒', '轻松', '1', '2', 'http://img-tailor.11222.cn/bcv/big/201706121724114087.jpg');
INSERT INTO `list` VALUES (158, '龙王归来', '我叫王浪，成王败寇的王，浪子回头的浪！！！', '凉皮不加糖', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1124767026437.jpg');
INSERT INTO `list` VALUES (159, '上门女婿叶辰（又名霸婿崛起，最强弃子）', '上门女婿叶辰入赘三年。       妻子骂废物，丈母娘瞧不起，亲戚冷眼旁观。　　起得比鸡早，睡得比狗晚。　　处处看人脸色。　　上门女婿怎么了？　　倒插门又怎么了？　　三十年河东三十年河西！　　金鳞岂是池中物？　　当初你们对我爱答不理，今天我让你们高攀不起！', '掘土小坦克', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1116617099379.jpg');
