INSERT INTO `list` VALUES (171, '头狼', '本来我只想老老实实的当条哈巴狗，可他们瞧不起我，硬生生的把我逼成一头狼！待到秋来九月八，我花开后百花杀。冲天香阵透长安，满城尽带黄金甲。', '寻飞', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1106998237451.jpg');
