INSERT INTO `list` VALUES (189, '重生之我能看到成功率', '重生到平行世界，林尘发现自己可以算出成功率，从此人生发生翻天覆地的巨变。我买彩票中头奖的概率？100%！我勾搭妹子成功的概率？100%！我成为世界首富的概率？100%！', '当仁不让', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1130808247575.jpg');
INSERT INTO `list` VALUES (190, '绝色总裁的贴身兵王', '兵王会医术，谁也挡不住！林轩奉师傅之命回归都市，肩负保护美女总裁的重任，两人也因此过上了“同居”生活。恩……女总裁虽然冷冰冰的，但林轩相信，自己的热情总能将她融化。然而天不遂人愿，各种各样的美女接二连三的闯入林轩的生活里，让他的生活，变得愈发的不平静……', '夜夜玩翻滚', '总裁', '1', '2', 'http://img-tailor.11222.cn/bcv/middle/201603311324445820.jpg');
INSERT INTO `list` VALUES (191, '仙府小神医', '辍学青年叶福贵，继承了父亲的小诊所，机缘巧合之下，获得了一座上古仙府，神农，采集，制药突飞猛进，更是随着新时代大学生美丽女友，一起发家致富奔小康，致富路上，医路飘香。', '老婆爱我', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1167507935334.jpg');
INSERT INTO `list` VALUES (192, '天行医尊', '一纸婚约，陈飞宇入世寻找未婚妻，从此奇遇不断。左手金针，世间疑难杂症无我不医；右手长剑，华夏各大豪族朽如草芥！念头通达无拘束，赚钱泡妞两不误。', '执笔问长生', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1120888240340.jpg');
INSERT INTO `list` VALUES (193, '都市最强赘婿', '一年前，他为了救一位身患白血病的富家千金，光荣的吃上了软饭。一年后，富家千金康复，砸了他的饭碗。他立志要吃首富家的软饭，打肿富家千金的脸，让她知道什么叫做高不可攀。', '小脚冰冷', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1196588245016.jpg');
INSERT INTO `list` VALUES (194, '都市大神戒', '神戒在手，美女我有。高中生宋砚自从得到一枚戒指后，生活就发生了翻天覆地的变化，为此，宋砚非常苦恼，面对诸多美女，是收了，还是收了，还是收了？', '不是蚊子', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1177008162187.jpg');
INSERT INTO `list` VALUES (195, '上门神医', '人在绝望中崛起，如触底反弹，带着一身狂气纵横都市。一手掌银针，一手握生死，我要将所有看不起我的人统统踩在脚下。', '南海煮鱼', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1131108012522.jpg');
INSERT INTO `list` VALUES (196, '极品上门女婿', '（主角名：秦浩、林冰婉、林若涵 ）三年前，秦浩送外卖的时候，被豪车撞倒了，没想到反被车主逼着跪在车前忏悔，被豪车大灯照了一个晚上，秦浩的双眼就此瞎了。从此，他悲惨的人生开始了；为了替父还债，秦浩成了上门女婿，受尽歧视鄙夷，过着憋屈的生活；三年之后，秦浩睁开双眼，一道神芒闪过。', '不吃鱼的猫儿', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1150887991215.jpg');
INSERT INTO `list` VALUES (197, '乡村小神农', '小农民强势归来，通医术、修法术、玩转都市，邂逅各路美女，无往不利！且看他如何逆天崛起，笑傲天下！', '南山侠', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1120028255320.jpg');
INSERT INTO `list` VALUES (198, '超级黄金指', '落魄花花大少偶得一根神奇手指，从此风云化龙，捡漏赌石，鉴宝品玉，赌场纵横，从一贫如洗到超级富豪，开启财富精彩人生。', '道门弟子', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1102217954392.jpg');
INSERT INTO `list` VALUES (199, '极品透视', '醒掌天下权，醉卧美人膝，乃是每一个男人的终极目标。一个普通青年，从一个老骗子的手中得一奇异石头，令人惊奇的是，借助石头，他竟然拥有了透视的能力，且看他如何玩转都市，笑傲人生。', '赤焰圣歌', '奇遇', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1184497460830.jpg');
INSERT INTO `list` VALUES (200, '我的傻白甜老婆（又名：首席继承人、超级人生）', '主角：陈平 江婉“不行啊，不努力就要回去继承万亿家产了。”陈平作为顶级豪门继承者，为此很烦恼。', '会抽烟的于大爷', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1161957910597.jpg');
INSERT INTO `list` VALUES (201, '狂野小农民', '美女我喜欢，快来帮我种田暖被。乡村孤儿，受到龙女的青睐，除村霸，踩恶少，带领村民勤劳致富，过上了幸福的生活。', '神话', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1179197150738.jpg');
INSERT INTO `list` VALUES (202, '极品上门女婿', '（主角名：秦浩、林冰婉、林若涵 ）三年前，秦浩送外卖的时候，被豪车撞倒了，没想到反被车主逼着跪在车前忏悔，被豪车大灯照了一个晚上，秦浩的双眼就此瞎了。从此，他悲惨的人生开始了；为了替父还债，秦浩成了上门女婿，受尽歧视鄙夷，过着憋屈的生活；三年之后，秦浩睁开双眼，一道神芒闪过。', '不吃鱼的猫儿', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1114308260393.jpg');
INSERT INTO `list` VALUES (203, '乡村小神农', '小农民强势归来，通医术、修法术、玩转都市，邂逅各路美女，无往不利！且看他如何逆天崛起，笑傲天下！', '南山侠', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1129418227535.jpg');
INSERT INTO `list` VALUES (204, '超级黄金指', '落魄花花大少偶得一根神奇手指，从此风云化龙，捡漏赌石，鉴宝品玉，赌场纵横，从一贫如洗到超级富豪，开启财富精彩人生。', '道门弟子', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1129908260383.jpg');
INSERT INTO `list` VALUES (205, '极品天医', '你是家财万贯，权势滔天的大人物？惹毛了我——不治！你是容颜无双，倾国倾城的女神？惹毛了我——不泡！你是出身显赫，实力强横的天之骄子？惹毛了我——猛踩！天医门第二十七代传人萧鸣来到都市之后，掀起了一场波澜壮阔的精彩逆袭之旅！', '真剑', '爽文', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1100247989046.jpg');
INSERT INTO `list` VALUES (206, '近身兵王', '★精华简介★ 秦渊，一个被敌人称为“凶兽”的特种兵王，在军队服役六年后重新回到都市，机缘巧合下成为东南地区黑道扛把子的女儿的贴身保镖，一段暧昧的旅程就此拉开。秦渊原本以为远离军队后可以过上轻松惬意的生活，谁曾想，一个沉寂二十多年的阴谋才刚刚浮出水面，逼迫他不得不继续走上铁与血的道路......', '月下吟', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1181538231572.jpg');
INSERT INTO `list` VALUES (207, '村野女人香', '充数小村医李应当，阴差阳错被打通奇经八脉，摇身一变成了方圆百里手到病除的小神医，最让他有成就感的是，运用他的能量带领金坑村的父老乡亲脱贫致富，将与世隔绝的贫困村建成了一个令人心驰神往的世外桃源……', '惊艳之谈', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1105867984364.jpg');
INSERT INTO `list` VALUES (208, '都市狂少', '一年前，叶凡为天之骄子，万众瞩目。一年后，他却跌落凡尘，受尽欺凌。一次意外，让叶凡走上了一条不同的道路，绝色校花投怀送抱，商界枭雄俯首称臣，武道宗师敬若神明。叶凡：我不懂什么叫年少轻狂，我只知道——胜者为王！', '陆通', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1133198254773.jpg');
INSERT INTO `list` VALUES (209, '龙抬头', '曾经看不起我的女神，现在来我的公司应聘……有朝一日虎归山，定叫血染半边天；有朝一日龙抬头，定让黄河水倒流！', '抚琴的人', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1166878244777.jpg');
INSERT INTO `list` VALUES (210, '最强上门女婿', '偶然成了天南第一美女的老公，天南第一豪阀的上门女婿，叶风本以为从此过上了老婆孩子热炕头的幸福生活，可惜老婆不让上床……', '水门绅士', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1168437983728.jpg');
INSERT INTO `list` VALUES (211, '不凡小仙农', '平凡小农张青山只想种种田，养养花，打打恶霸。但自从得到一只小仙葫后，日子就变得不平凡了。他种的韭菜效果出众，他种的黄瓜威力惊人，就连他家门前池塘的小青蛇也不知何时长出了角。', '陈家有虎', '热血', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1102288258509.jpg');
INSERT INTO `list` VALUES (212, '超级继承人', '当苏北拿出全家的家当来到女友家准备提亲时，女友家却临时加价，苏北无奈被迫分手。　　让所有人意想不到的是，苏北居然是顶级财团继承人。　　从此，苏北的人生变得不一样了，纵横商界，打脸各大豪门，获得顶级美女倾心，更是站在了这个世界的巅峰，睥睨天下。', '三七', '扮猪吃虎', '1', '2', 'http://img-tailor.11222.cn/bcv/big/1125257963867.jpg');
