INSERT INTO `list` VALUES (315, '天师下山', '五年前，家族覆灭，陈霆被迫上山当了道士，苟延残喘；五年后，他被尊为龙虎山小天师，武道擎天，术法通玄，强势回归，势必要拿回失去的一切！昔日仇家：“你是修道之人，怎能杀我？”“我修的是顺心意，杀你，才是修道！”', '白马非马', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1159538298921.jpg');
INSERT INTO `list` VALUES (316, '成神的亿万种选项', '一觉醒来就变成了南楚帝国的六皇子？摆在林枫面前的，不是享不尽的荣华富贵，而是祸在旦夕的艰险境遇！想要解决，唯有变强！遇到难题不用急，无敌选项来帮您！【选项一：万界抽奖】【选项二：提升资质】【选项三：超级灵宝】......提升修为便可增加选项，你想要的，全！都！有！', '青衫隐', '爽文', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1134218298926.jpg');
INSERT INTO `list` VALUES (317, '最强纨绔系统', '神级“盗贼”玩家许流苏意外穿越，携《最强纨绔系统》重生万疆大陆。却没想到遭遇了无穷无尽的装逼犯。许流苏很爽，因为系统必须装逼泡妞才能获得奖励！“神通功法、丹道造化、圣级铭文、天赋血脉？”呵呵，不好意思，我随便兑换个上古神能就能吊打你们。且看许流苏如何玩转纨绔系统，执掌三界人物，手握日月星辰！', '梁一笑', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1119498298974.jpg');
INSERT INTO `list` VALUES (318, '重生之我变成了火星', '重生成了火星？第一阶段科技进化，第二阶段高魔进化，第三阶段超魔进化！宇宙之间除了人类还有无数可怕存在，让我带领人类征服宇宙，我们的目标是星辰大海~', '仰望黑夜', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/middle/201603311324445820.jpg');
INSERT INTO `list` VALUES (319, '御天武帝', '（又名：万道至尊）（主角名：楚岩、柳倾城）大道无形，大道无情。少年楚岩，怀一颗赤子之心，为心中所梦不惜踏破山河，破灭三千世界，终成一代御天武帝。', '晓浅', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1134428296166.jpg');
INSERT INTO `list` VALUES (320, '亘古大帝', '万年前，林焱一日称帝，镇压妖族大帝，却被兄弟背叛斩杀、夺走帝基，更背负人族叛徒的万年骂名。万年后，林焱重生，纵凡体，此一世，也将踏平万古，成就亘古大帝！', '陈辉', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1131867989049.jpg');
INSERT INTO `list` VALUES (321, '神国之上', '“这是我的剑，也是我的棺。”十六岁那年，宁长久白衣悬剑，如是说道。', '见异思剑', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1107938289828.jpg');
INSERT INTO `list` VALUES (322, '大唐第一闲人', '重生于大唐盛世，萧珪只想阅尽繁华、逍遥自在；携美终老、百子千孙。做一个既不太累、也不太忙的，富贵大闲人。', '萧玄武', '轻松', '2', '1', 'http://img-tailor.11222.cn/bcv/middle/201603311324445820.jpg');
INSERT INTO `list` VALUES (323, '穿越了的学霸', '一个宅男学霸因为一次意外穿越到了古代，还悲催的穿越到了一个家奴的身上。为了生存，为了能改变自己的境况，他凭借自己的聪明才智，居然在这个时代混的风生水起，但是随着声名鹊起，危机也随之而来……', '暗夜茗香', '技术流', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1157128299184.jpg');
INSERT INTO `list` VALUES (324, '全球进化', '平凡少年，再次重生。　　却发现这个世界有武者，有灵考。　　而他正面对富家子弟的陷害和为难。　　正在进退两难之际，却获得逆天系统，只要有钱，气血就能无限提升。　　升级，只在于一念之间。　　他一步一步，走向至强巅峰，以无上之资，横压当世，活出了不一样的人生！', '剑公子', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1149648171769.jpg');
INSERT INTO `list` VALUES (325, '御天武帝', '（又名：万道至尊）（主角名：楚岩、柳倾城）大道无形，大道无情。少年楚岩，怀一颗赤子之心，为心中所梦不惜踏破山河，破灭三千世界，终成一代御天武帝。', '晓浅', '热血', '2', '1', 'http://img-tailor.11222.cn/bcv/big/1176707962032.jpg');
