INSERT INTO `list` VALUES (425, '龙纹战神', '天下第一圣重生百年后，修无上神功，争霸天下，别跟我比炼丹，十成丹随手就来，别跟我比晋级速度，羞死你我可不负责，别跟我比修炼经验，我是老祖，江尘的存在，注定要羞煞万千天才……', '苏月夕', '重生', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1139228270110.jpg');
INSERT INTO `list` VALUES (426, '一键修炼系统瞬间百万级', '“叮！第一项功能激活，开启自动修炼，修炼速度等于超级妖孽！”“叮！功能升级，修炼速度翻倍，功能再升级，修炼速度再翻倍！”“叮！第二项功能激活，开启自动习武......”“叮！第三项功能激活......”修炼很难？不好意思我修炼一天等于你修炼几十年！练功很难？不好意思我什么都不干就能轻松学会无数功法！炼丹很难？不好意思我丹药吃不完都快生霉了！......苏浪穿越后获得一键修炼系统，每秒都在变强！从现在开始，吊打一切，天下无仇！', 'D猫', '热血', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1180317205795.jpg');
INSERT INTO `list` VALUES (427, '至尊丹帝', '★精华简介★ 【阿里文学原创】丹尊陆宇炼制仙丹失败，之后重生成为世家少主，有前世无数神功秘法和炼丹记忆，有今生重修打下的坚实基础，陆宇注定要踏出一条辉煌大路，踏丹道巅峰，名扬天下，美人倾心，斩破天地，逍遥长生。还有什么能够挡得住他的崛起？！', '通林', '重生', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1158368268318.jpg');
INSERT INTO `list` VALUES (428, '斗罗大陆续集之史莱克七怪成神之路', '双神出，天地哭；千重劫，万世难；人间乱，反叛患；神界危，神王归；天机泄，源泉枯；幽冥动，祸根现……', '洛星晨', '历险', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1158198280057.jpg');
INSERT INTO `list` VALUES (429, '神祇', '天蛮大陆，武道昌盛。少年苏逸携万妖逆袭崛起，临驾一个个万年世家大族之上，迎娶白富美，走上异世巅峰，朝向太古神祇之位！', '禹枫', '热血', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1122747956784.jpg');
INSERT INTO `list` VALUES (430, '百炼飞升录', '山村少年，历经种种，加入落霞宗，开始修真路，意外服下的无名朱果，将给他带来怎样的机遇？以赤子之心入道，又将经历怎样的诡异与浮沉？独身闯荡大陆，历经万千艰险，既已选择了非人之路，便永无回头。仙路茫茫，美人如画，朋友？知己？漫长仙途路，千锤百炼，终飞升仙界巅峰！无穿越，不重生，血色拼搏，终登巅峰！', '虚眞', '热血', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1164257984493.jpg');
INSERT INTO `list` VALUES (431, '丹师剑宗', '热血青年手握猥琐剑法，炼丹副业威名赫赫，坐拥后宫佳丽三千，发誓成为史上最变态强暴的丹师剑宗。', '古栋', '废材流', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1165928053942.jpg');
INSERT INTO `list` VALUES (432, '神级大昏君系统', '强抢民女？昏君！修建宫殿？昏君！斗鸡赛马？昏君！现代青年严浩穿越到异界成了皇帝，获得大昏君系统。每完成一个昏君任务他都可以获得丰厚奖励，从此以后他就走上了漫漫昏君之路。可渐渐大家却发现，别的昏君亡国，而在他这个昏君的带领下，国家竟然日益兴旺。', '苟心', '爽文', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1130227867702.jpg');
INSERT INTO `list` VALUES (433, '龙血战神', '★精华简介★ 上古世纪，天地初开，龙祭大陆乃神龙一族之天下，时至今日神龙灭绝殆尽，神秘浩劫再度降临，天下苍生岌岌可危！少年龙辰，背负父辈荣耀使命，追逐神龙灭绝谜团。机缘巧合之下，成为亿万年来唯一的祖龙武者，问鼎龙族至尊，号令天下神龙。最热血战斗，最感人柔情，血剑暴杀，神龙摆尾，龙威浩荡，傲立天穹！浩劫降临之日，亿万龙族再度归来，旷世之战，一触即发！吾辈太古龙族，甘愿血染青天，只为兄弟情义；挥手倒转星河，只为美人如诗；吾等让众神陨落，天道崩裂，只为天下芸芸众生！吞天族，我们回来了！', '风青阳', '热血', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1176768274681.jpg');
INSERT INTO `list` VALUES (434, '遮天记', '陆安是一个弃婴，准确的说是要被父亲扼杀的孩子。于古江之水漂流到最贫瘠的地方，成为了最低等的奴隶。食物，住所，还有尊严，都成为奢侈品。于是，他身着麻衣，行走与蛮荒各处，行走于诸侯列国，行走于十五宗十六门，最后站在八古与天神面前。他从奴隶窟中生长，后来一手遮天。', '归来的洛秋', '热血', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1194428286974.jpg');
INSERT INTO `list` VALUES (435, '神级大昏君系统', '强抢民女？昏君！修建宫殿？昏君！斗鸡赛马？昏君！现代青年严浩穿越到异界成了皇帝，获得大昏君系统。每完成一个昏君任务他都可以获得丰厚奖励，从此以后他就走上了漫漫昏君之路。可渐渐大家却发现，别的昏君亡国，而在他这个昏君的带领下，国家竟然日益兴旺。', '苟心', '爽文', '2', '2', 'http://img-tailor.11222.cn/bcv/big/1170208059224.jpg');
