INSERT INTO `list` VALUES (883, '绝品相师', '他——是来自鼠山的江枫叶，一个精通相术和医术的高手，一个命运多舛的善良小子，且看他如何搅起这片动荡风云。', '烟云雨起', '爽文', '4', '2', 'http://static.zongheng.com/upload/cover/1c/11/1c1105bab6fd3721aa6b9ec52b28e6ab.jpeg');
