INSERT INTO `list` VALUES (2880, '爱你是我难言的痛', '（又名：温情一生只为你）（主角名：林帘、湛廉时）林帘嫁给了富可敌国的湛廉时，以贫民的身份，所有人都说她上辈子烧了高香才会嫁给这么优秀的男人，她也这么认为。然后，一年婚姻，他疼她，宠她，惜她。她爱上了他。', '酒卿悠玥', '虐恋', '12', '2', 'http://static.zongheng.com/upload/cover/77/c5/77c5a12294b610cf0bc855dd839f818c.jpeg');
